def array_size(array)
	puts "Combien y a-t-il d'handle dans ce tableau?"
	puts array.length
end

def handles_with_number(array)
	puts "Combien d'handle contiennent un numéro?"
	handle_with_number= 0
	array.each do |handle|
		if handle.index /[0-9]/
			handle_with_number +=1
		end
	end	
	puts handle_with_number
end

def number_of_aude(array)
	puts "Combien d'handle contiennent 'aude'?"
	number_aude = 0	
	array.each do |handle|
		if handle.downcase.include? "aude"
			number_aude +=1
		end
	end	
	puts number_aude
end

def first_letter_is_uppercase(array)
	puts "Combien d'handle commencent avec une majuscule? après le @"
	number_uppercase_first = 0
	array.each do |handle|
		lettres = handle.split(//)
		if lettres[1].index /[A-Z]/
			number_uppercase_first+=1
		end
	end	
	puts number_uppercase_first	
end	

def handles_with_uppercase(array)
	puts "Combien d'handle contienent une majuscule?"
	number_uppercase_handle = 0
	array.each do |handle|
		if handle.index /[A-Z]/
			number_uppercase_handle +=1
		end
	end	
	puts number_uppercase_handle	
end	

def number_of_underscore(array)
	puts "Combien d'underscore y a-t-il dans le tableau?"
	number_underscore = 0
	array.each do |handle|
		letter = handle.split(//)
		letter.each do |x|
			if x.index /[_]/
				number_underscore +=1
			end
		end
	end	
	puts number_underscore	
end	

def sort_A_Z(array)
	puts "Voici le tableau classé par ordre alphabétique"
	puts array.sort_by {|handle| handle.downcase}
end	

def sort_size(array)
	puts "Voici les 50 handles les plus courts du tableau:"
	puts array.sort_by {|handle| handle.size}.take(50)
end	

def index_handle(array)
	puts "L'handle @epenser a pour index: "
	puts array.index ("@epenser")
end

array_size(array)
handles_with_number(array)
number_of_aude(array)
first_letter_is_uppercase(array)
handles_with_uppercase(array)
number_of_underscore(array)
sort_A_Z(array)
sort_size(array)
index_handle(array)

